import React, { useState, useEffect } from "react";
import Axios from "axios";

export default function NormalUser() {
  const [userx, setUserX] = useState([]);
  const [visible, setVisible] = useState(true);
  const [newUser, SetNewUser] = useState("");
  const [userId, setUserID] = useState("");
  const [alertx, setAlertX] = useState(true)

  Axios.defaults.withCredentials = true;

  const edit = async () => {
    try {
      setVisible(!visible);
      //window.location.reload(false);
      const res = await Axios.post("http://localhost:3001/api/changeUname", {
        oldUser: userx[0].username,
        newusername: newUser,
        newid: userId,
      });
      console.log(res.data);
      setUserX(
        userx.map((item) =>
          item.id === userId ? { ...item, username: res.data } : item
        )
      );
      setAlertX(false);
      setTimeout(() => {
        setAlertX(true)
      }, 3000);
    } catch (err) {
      console.log(err);
    }
  };

  const vis = () => {
    setVisible(!visible);
  };

  useEffect(() => {
    getUser();
  }, [newUser]);

  const getUser = async () => {
    const res = await Axios.get("http://localhost:3001/api/login");
    if (res.data.loggedIn === true) {
      setUserX(res.data.user);
      console.log(res.data.user[0].username);
      setUserID(res.data.user[0].id);
    }
  };

  return (
    <div>
      {userx.map((val) => {
        return (
          <div className="zz" key={val.id}>
            <h1>
              Kullanıcı Adın:{" "}
              {visible ? (
                val.username
              ) : (
                <>
                  {" "}
                  <input
                    onChange={(e) => {
                      SetNewUser(e.target.value);
                    }}
                    placeholder={val.username}
                  />
                  <button onClick={edit}>Güncelle</button>{" "}
                </>
              )}
            </h1>
            {alertx ? "" : (<div className="alert alert-success" role="alert">
              Kullanıcı adını başarıyla değiştirdin!
            </div>)}
            <button className="btn btn-outline-info" style={{right: "88%"}} onClick={vis}>Kullanıcı adını değiştir</button>
            <br/>
            <br/>
            <h1>Rolün: {val.role}</h1>
          </div>
        );
      })}
      <img src="https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331257__340.png"></img>
    </div>
  );
}
