import React, { useState, useEffect } from "react";
import Axios from "axios";

export default function NormalUser() {
  const [userx, setUserX] = useState([]);

  Axios.defaults.withCredentials = true;

  useEffect(() => {
    Axios.get("http://localhost:3001/api/login").then((response) => {
      if (response.data.loggedIn === true) {
        setUserX(response.data.user);
      }
    });
  }, [userx]);

  return (
    <div>
      {userx.map((val) => {
        return (
          <div key={val.id}>
                    <h1>Kullanıcı Adın: {val.username}</h1> <button>Edit</button>
                    <h1>Rolün: {val.role}</h1><button>Edit</button>
                
          </div>
        );
      })}
      <img src="https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331257__340.png"></img>
    </div>
  );
}
