import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useHistory } from "react-router-dom";

export default function MainPage() {
  const [postList, setPostList] = useState([]);
  const [role, setRole] = useState("");

  
  let history = useHistory();




  Axios.defaults.withCredentials = true;

  useEffect(() => {
      Axios.get("http://localhost:3001/api/login").then((response) => {
        if (response.data.loggedIn === true) {
          setRole(response.data.user[0].role);
        }
      });
    Axios.get("http://localhost:3001/api/get/").then((data) => {
      setPostList(data.data);
    });
  }, []);

  const likePost = (id) => {
    Axios.post(`http://localhost:3001/api/like/${id}`).then((response) => {
      alert("you liked the post");
    });
  };

  return (
    <div className="MainPage">
      <div className="PostContainer">
        {postList.map((val, key) => {
          return (
            <div
              className="Post"
              key={key}
              onClick={() => {
               //history.push(`/post/${val.id}`);
              }}
            >
              <h1>{val.title}</h1>
              <p>
                {val.post_text.length > 500
                  ? val.post_text.substring(0, 500) + "..."
                  : val.post_text}
              </p>

              <button 
                className="btn-success bi bi-emoji-heart-eyes"
                onClick={() => {
                  likePost(val.id);
                }}
              >
                Like
              </button>
              {role === "admin" ? (
            <button className="btn-success bi bi-emoji-heart-eyes" onClick={deletex}>Sil</button>
          ) : (
            ""
          )}
              
              <div className="bottom">
              <h4>Yazan: {val.user_name}</h4>
              <h4>{val.likes} like</h4>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
