import { createSlice } from "@reduxjs/toolkit";
import { useSelector } from "react-redux";

export const userSlice = createSlice({
  name: "user",
  initialState: {
    user: null,
  },
  reducers: {
    loginz: (state, action) => {
      state.user = action.payload;
    },
    logoutz: (state) => {
      state.user = null;
    },
  },
});

export const { loginz, logoutz } = userSlice.actions;

export const selectUser = useSelector((state) => state.user.user);

export default userSlice.reducer;
