import React, { useEffect, useState } from "react";
import "./App.css";
import { BrowserRouter as Router, Route } from "react-router-dom";
import MainPage from "./pages/MainPage";
import CreatePost from "./pages/CreatePost";
import Post from "./pages/Post";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import Profile from "./pages/Profile";
import AllUsers from "./pages/AllUsers";
import { logoutz } from "./reducer/infuser";
import { islogged } from "./action/index";
import { useDispatch } from "react-redux";
import Axios from "axios";
import SideBar from "./SideBar/SideBar";

function App(props) {
  const [role, setRole] = useState("");

  const dispatch = useDispatch();

  Axios.defaults.withCredentials = true;

  const logout = () => {
    Axios.get("http://localhost:3001/api/logout");

    dispatch(logoutz());
    dispatch(islogged());
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/api/login").then((response) => {
      if (response.data.loggedIn === true) {
        //console.log(response)
        setRole(response.data.user[0].role);
      }
    });
  }, []);

  return (
    <>
    <div className="Navbar">
    <div style={{backgroundColor:"black",}}class="dropdown">
  <a class="btn btn-secondary dropdown-toggle" href="/" id="dropdownMenuLink" data-bs-toggle="dropdown" aria-expanded="false">
    Dropdown link
  </a>

  <ul class="dropdown-menu" aria-labelledby="dropdownMenuLink">
    <li><a className="dropdown-item" href="/">Action</a></li>
    <li><a class="dropdown-item" href="/">Another action</a></li>
    <li><a class="dropdown-item" href="/">Something else here</a></li>
  </ul>
</div>
        <div className="links">
        <img alt="de" style={{position:"absolute", right:"97%", top:"0%", height:"60px", width:"60px"}}src="https://i.hizliresim.com/ABncva.png"></img>

        {role === "admin" ? <a href="/AllUsers">Kullanıcılar</a> : "" }
        {role === "visitor" || role === "admin" || role === "mod" ? "" : <a href="/Register">Kaydol</a> }
        {role === "visitor" || role === "admin" || role === "mod" ? "" : <a href="/Login">Giriş Yap</a>  }
        {role === "visitor" || role === "admin" || role === "mod" ? (<a href="/Profile">Profil</a>) : "" }
        {role === "visitor" || role === "admin" || role === "mod" ? (<a href="/" onClick={logout}>Çıkış Yap</a>) : "" }
        </div>
      </div>
      <div className="App">
        {role === "visitor" || role === "admin" || role === "mod" ? (<SideBar />) : (<div className="SidebarX"></div>)}
      </div>

      )
      <Router>
        <Route path="/" exact render={(props) => <RegisterPage />} />
        <Route path="/Register" render={(props) => <RegisterPage />} />
        <Route path="/HomePage" render={(props) => <HomePage />} />
        <Route path="/Login" render={(props) => <LoginPage />} />
        <Route path="/posts" render={(props) => <MainPage />} />
        <Route path="/createpost" render={(props) => <CreatePost />} />
        <Route path="/post/:postId" render={(props) => <Post />} />
        <Route path="/Profile" render={(props) => <Profile />} />
        <Route path="/AllUsers" render={(props) => <AllUsers />} />
      </Router>
    </>
  );
}

export default App;
