import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { islogged } from "../action/index";
import { loginz } from "../reducer/infuser";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [id] = useState("");
  const [role] = useState("");
  const [userr, setUserr] = useState([]);
  const [loginStatus, setLoginStatus] = useState("");

  const isLogged = useSelector((state) => state.isLogged);

  const dispatch = useDispatch();
  let history = useHistory();

  Axios.defaults.withCredentials = true;

  const login = () => {
    Axios.post("http://localhost:3001/api/login", {
      username: username,
      password: password,
    }).then((response) => {
      if (response.data.message) {
        console.log(loginStatus);
        setLoginStatus(response.data.message);
      } else {
        setTimeout(() => {
          dispatch(
            loginz({
              id: response.data[0].id,
              username: username,
              password: password,
              role: response.data[0].role,
            })
          );
          dispatch(islogged());
          window.location.reload();
          history.push("/");
        }, 600);

        //console.log(response.data[0])
      }
    });
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/api/login").then((response) => {
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data.user[0].username);
      }
    });
  }, []);

  return (
    <form>
      {/*{userr.map((item) => {
       <div>{setId(item.id)}{setRole(item.role)}</div>
      })} */}
      <div className="mb-3">
        <label className="form-label">KullanÄ±cÄ± AdÄ±</label>
        <input
          type="email"
          className="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          onChange={(e) => {
            setUsername(e.target.value);
          }}
        />
        <div id="emailHelp" className="form-text"></div>
      </div>
      <div className="mb-3">
        <label htmlFor="exampleInputPassword1" className="form-label">
          Åžifre
        </label>
        <input
          type="password"
          className="form-control"
          id="exampleInputPassword1"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
        <div id="emailHelp" className="form-text">
          {"HoÅŸgeldin " + loginStatus + role + id}
        </div>
      </div>
      <button type="submit" className="btn btn-success" onClick={login}>
        GiriÅŸ Yap
      </button>
    </form>
  );
}
