import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { editz, selectUser } from "../reducer/infuser";

export default function NormalUser() {
  const [userx, setUserX] = useState([]);
  const [visible, setVisible] = useState(true);
  const [newUser, SetNewUser] = useState("");
  const [userId, setUserID] = useState("");
  const [alertx, setAlertX] = useState(true);

  const user = useSelector(selectUser);
  const dispatch = useDispatch();

  Axios.defaults.withCredentials = true;

  const edit = async () => {
    
    try {
      setVisible(!visible);
      window.location.reload(false);
      const res = await Axios.post("http://localhost:3001/api/changeUname", {
        oldUser: userx[0].username,
        newusername: newUser,
        newid: userId,
      });
      console.log(userx[0]);
      setUserX(
        userx.map((item) =>
          item.id === userId ? { ...item, username: res.data } : item
        )
      );
      dispatch(
        editz({
          id:userx[0].id,
          username: newUser,
          password: userx[0].password,
          role: userx[0].role,
          loggedIn: true,
        })
      );
      setAlertX(false);
      setTimeout(() => {
        window.location.reload(false);
        setAlertX(true);
      }, 3000);
    } catch (err) {
      console.log(err);
    }
  };

  const vis = () => {
    setVisible(!visible);
    
  };

  useEffect(() => {
    getUser();
  }, [newUser]);

  const getUser = async () => {
    const res = await Axios.get("http://localhost:3001/api/login");
    if (res.data.loggedIn === true) {
      setUserX(res.data.user);
      setUserID(res.data.user[0].id);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#ECDBCF",
        height: "900px",
        fontFamily: "fantasy",
      }}
    >
      
        
          <div className="zz" key={user.id}>
            <h1>
              Kullanıcı Adın: {""}
              {visible ? (
                user.username
              ) : (
                <>
                  {" "}
                  <input
                    style={{
                      position: "absolute",
                      height: "5%",
                      width: "20%",
                      top: "9%",
                      right: "66%",
                    }}
                    type="email"
                    className="form-control"
                    id="exampleInputEmail1"
                    aria-describedby="emailHelp"
                    onChange={(e) => {
                      SetNewUser(e.target.value);
                    }}
                    placeholder={user.username}
                  />
                  <button
                    className="btn btn-outline-success"
                    style={{ right: "60.8%", top: "7.9%", height: "5%" }}
                    onClick={edit}
                  >
                    Güncelle
                  </button>{" "}
                </>
              )}
            </h1>
            {alertx ? (
              ""
            ) : (
              <div
                style={{
                  position: "absolute",
                  right: "65%",
                  top: "15.7%",
                  width: "25%",
                }}
                className="alert alert-success"
                role="alert"
              >
                KULLANICI ADINI BAŞARIYLA DEĞİŞTİRDİN!
              </div>
            )}
            <button
              className="btn btn-outline-info"
              style={{ right: "90%" }}
              onClick={vis}
            >
              Kullanıcı adını değiştir
            </button>
            <br />
            <br />
            <br />
            <h1>Rolün: {user.role}</h1>
          </div>
        );
      
      <img
        alt="de"
        src="https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331257__340.png"
      ></img>
    </div>
  );
}
