import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useHistory } from "react-router-dom";
import { useSelector } from "react-redux";
import { selectUser } from "../reducer/infuser";
import { AiFillLike } from "react-icons/ai";
import { AiFillDislike } from "react-icons/ai";
import { BsHeartFill } from "react-icons/bs";

export default function MainPage() {
  const [postList, setPostList] = useState([]);
  const [role, setRole] = useState("");

  const user = useSelector(selectUser);

  let history = useHistory();

  Axios.defaults.withCredentials = true;

  useEffect(() => {
      Axios.get("http://localhost:3001/api/login").then((response) => {
        if (response.data.loggedIn === true) {
          setRole(response.data.user[0].role);
        }
      });
    Axios.get("http://localhost:3001/api/get/").then((data) => {
      setPostList(data.data);
      console.log(data.data)
    });
  }, []);

  const likePost = (id) => {
    Axios.post(`http://localhost:3001/api/like/${id}`).then((response) => {
      alert("you liked the post");
    });
  };

  const deletex = (id) => {
    Axios.post(`http://localhost:3001/api/delete/${id}`)
      setTimeout(() => {
        window.location.reload(false);
      }, 200);
    }
  

  return (
    <div className="Appx">
      <div className="PostContainer">
        {postList.map((val, key) => {
          return (
            <div
              className="Post"
              style={{border:"1px solid black"}}
              key={key}
              onClick={() => {
               history.push(`/post/${val.id}`);
              }}
            >
              <div  className="bottom">
              <h4>{user.username}</h4>
              
              </div>
              <h1>{val.title}</h1>
              <p>
                {val.post_text.length > 500
                  ? val.post_text.substring(0, 500) + "..."
                  : val.post_text}
              </p>
              <BsHeartFill style={{position:"relative",right:"48.8%",top:"27px",color:"#633b86"}}/>
              <div style={{display:"flex",fontSize:"20px"}}> <BsHeartFill style={{position:"relative",right:"90%"}}/> <strong>{val.likes} like</strong></div>

              <div className="likes"><div style={{position:"absolute",right:"50%",width:"460px",border:"1px solid black",color:"white"}} className="row"><AiFillLike onClick={() => {
                  likePost(val.id);
                }}/> BeÄŸen 
                </div>
                </div>
                <div className="likes" style={{position:"absolute",right:"4%",width:"459px",border:"1px solid black",color:"white"}}>
                  <div className="row">
                <AiFillDislike />
                disslike 
                </div>
                </div>
                
              
              {role === "admin" ? (
            <button className="btn-success bi bi-trash" onClick={() => {
              deletex(val.id);
            }}>Sil</button>
          ) : (
            ""
          )}
              
              
              
            </div>
          );
        })}
      </div>
    </div>
  );
}
