import React, { useEffect, useState } from "react";
import "./App.css";
import { BrowserRouter as Router, Route } from "react-router-dom";
import MainPage from "./pages/MainPage";
import CreatePost from "./pages/CreatePost";
import Post from "./pages/Post";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import Profile from "./pages/Profile";
import AllUsers from "./pages/AllUsers";
import { logoutz } from "./reducer/infuser";
import { islogged } from "./action/index";
import { useDispatch } from "react-redux";
import Axios from "axios";


function App(props) {
  const [role, setRole] = useState("");

  const dispatch = useDispatch()

  Axios.defaults.withCredentials = true;

  const logout = () => {

    Axios.get("http://localhost:3001/api/logout")

    dispatch(logoutz());
    dispatch(islogged());

  };

  useEffect(() => {
    Axios.get("http://localhost:3001/api/login").then((response) => {
      if (response.data.loggedIn === true) {
        //console.log(response)
        setRole(response.data.user[0].role);
      }
    });
  }, []);

  return (
    <>


     {role === "visitor" || role === "admin" || role === "mod" ? (<div className="App">
        <div className="sidebar">

          {role === "visitor" || role === "admin" || role === "mod" ? (
            <a href="/HomePage">Anasayfa</a>
          ) : (
            <a href="/HomePage">Anasayfa</a>
          )}
          <br></br>
          {role === "visitor" || role === "admin" || role === "mod" ? (
            <a href="/posts">Postlar</a>
          ) : (
            ""
          )}
          <br></br>
          {role === "visitor" || role === "admin" || role === "mod" ? (
            <a href="/createpost">Post gönderme</a>
          ) : (
            ""
          )}
          <br></br>
        </div>
        <div className="xd">
          {role === "visitor" || role === "admin" || role === "mod" ? (
            ""
          ) : (
            <a href="/Register">Kaydol</a>
          )}
          <br></br>
          {role === "visitor" || role === "admin" || role === "mod" ? (
            ""
          ) : (
            <a href="/Login">Giriş Yap</a>
          )}
          <br></br>

          {role === "admin" ? (
            <a href="AllUsers">Kullanıcılar</a>
          ) : (
            ""
          )}
          <br></br>


          {role === "visitor" || role === "admin" || role === "mod" ? (
            <a href="Profile">Profil</a>
          ) : (
            ""
          )}
          <br></br>
          {role === "visitor" || role === "admin" || role === "mod" ? (
            <a href="/" onClick={logout}>Çıkış Yap</a>
          ) : (
            ""
          )}
        </div>
      </div>) : (<div className="App">
        <div className="sidebar">
        <img alt="de" style={{position:"absolute", right:"95%", top:"2%", height:"80px", width:"100px"}}src="https://i.hizliresim.com/ABncva.png"></img>
        <a style={{position:"absolute", right:"0.2%", top:"15%"}} href="/Login">Giriş Yap</a>
        <a style={{position:"absolute", right:"6.4%", top:"15%"}}href="/Register">Kaydol</a>
        </div>
      </div>
        )} 

      <Router>
        <Route path="/" exact render={(props) => <RegisterPage />} />
        <Route path="/Register" render={(props) => <RegisterPage />} />
        <Route path="/HomePage" render={(props) => <HomePage />} />
        <Route path="/Login" render={(props) => <LoginPage />} />
        <Route path="/posts" render={(props) => <MainPage />} />
        <Route path="/createpost" render={(props) => <CreatePost />} />
        <Route path="/post/:postId" render={(props) => <Post />} />
        <Route path="/Profile" render={(props) => <Profile />} />
        <Route path="/AllUsers" render={(props) => <AllUsers />} />
      </Router>
    </>
  );
}

export default App;
