import React, { useState, useEffect } from "react";
import Axios from "axios";

export default function NormalUser() {
  const [userx, setUserX] = useState([]);

  Axios.defaults.withCredentials = true;

  useEffect(() => {
    Axios.get("http://localhost:3001/api/login").then((response) => {
      if (response.data.loggedIn === true) {
        setUserX(response.data.user);
      }
    });
  }, [userx]);

  return (
    <div>
      {userx.map((val) => {
        return (
          <div key={val.id}>
              <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>{val.username}</td>
      <td>@mdo</td>
    </tr>
    </tbody>)
  </table>
            <h1>Kullanıcı Adı: {val.username}</h1>
            <h1>Role: {val.role}</h1>{" "}
          </div>
        );
      })}
      <img src="https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331257__340.png"></img>
    </div>
  );
}
