  let { postId } = useParams();

  useEffect(() => {
    Axios.get(`http://localhost:3001/api/getFromId/${postId}`).then((data) => {
      console.log(data);
    });
  }, []);

  return (
    <div
      className="Post"
      onClick={() => {
        history.push(`/post/${val.id}`);
      }}
    >
      <p>{val.title}</p>
      <h4>
        {val.post_text.length > 500
          ? val.post_text.substring(0, 500) + "..."
          : val.post_text}
      </h4>
      {/*<h1>{val.user_name}</h1>*/}
    </div>
  );
}
