import React, { useState, useEffect } from "react";
import Axios from "axios";

export default function NormalUser() {
  const [userx, setUserX] = useState([]);
  const [visible, setVisible] = useState(true);
  const [newUser, SetNewUser] = useState("");
  const [userId, setUserID] = useState("");

  Axios.defaults.withCredentials = true;

  const edit = () => {
    setVisible(!visible);
    window.location.reload(false);
    Axios.post("http://localhost:3001/api/change", {
      newusername: newUser,
      newid: userId
    }).then((response) => {
      
      console.log(response);
    });
  };

  const vis = () => {
    setVisible(!visible);
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/api/login").then((response) => {
      if (response.data.loggedIn === true) {
        setUserX(response.data.user);
        setUserID(response.data.user[0].id)

      }
    });
  }, [userx]);

  return (
    <div>
      {userx.map((val) => {
        return (
          <div key={val.id}>
            <h1>
              Kullanıcı Adın:{" "}
              {visible ? (
                val.username
              ) : (
                <>
                  {" "}
                  <input
                    onChange={(e) => {
                      SetNewUser(e.target.value);
                    }}
                    placeholder={val.username}
                  />
                  <button onClick={edit}>Güncelle</button>{" "}
                </>
              )}
            </h1>
                
            <button onClick={vis}>Edit</button>

            <h1>Rolün: {val.role}</h1>
            <button>Edit</button>
          </div>
        );
      })}
      <img src="https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331257__340.png"></img>
    </div>
  );
}
