import axios from 'axios';
import React, { useState, useEffect } from 'react'
import { useSelector } from "react-redux";
import { selectUser } from "../reducer/infuser";

export default function RememberMe() {

    const [rememberP, setRememberP] = useState("");
    const [rememberPass, setRememberPass] = useState("");
    const [idx, setIdx] = useState("");
    const [savedP, setSavedP] = useState("");
    const [savedPass, setSavedPass] = useState("");
    const [hidd, setHidd] = useState(true)

    const user = useSelector(selectUser);

    const Sendx = () => {
        axios.post("http://localhost:3001/api/remember", {
            remember: rememberP,
            rememberpass: rememberPass,
            idx: idx,
        }).then((response) => {
            console.log(response)
            
        })
        setTimeout(() => {
            window.location.reload(false);
        }, 500);
    }

    useEffect(() => {
        setIdx(user.id)
    }, [])

    const Savex = () => {
        setHidd(false)
        axios.post("http://localhost:3001/api/rememberr", {
            idx: idx,
        }).then((response) => {
            setSavedP(response.data[0].remember)
            setSavedPass(response.data[0].rememberpassword)
            console.log(savedP)
        })
    }


    return (
        
        <div className="Appx">
            <h1 style={{fontSize:"50px"}}>Unuttuğun Şifrelerini Kaydet<span className="badge bg-secondary">Yeni Özellik</span></h1>
            <label>Platform:</label>
            <input placeholder="Platform adı" onChange={(e) => {setRememberP(e.target.value)}}/>
            <label>Şifren:</label>
            <input placeholder="Şifren" onChange={(e) => {setRememberPass(e.target.value)}}/>
            <br/>
            <button onClick={Sendx}>Kaydet</button>

            <button onClick={Savex}>Kaydettiklerimi Göster</button>
            <br/>
            
           {hidd ? "" : (<h1>Platform: {savedP} | Şifren: {savedPass} </h1>) }
        </div>
    )
}
